<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Percobaan extends MY_Controller {        public function  __construct() {         parent::__construct();        }		public function index() {		$this->load->view('hasil_coba');		}			function cari() {		// process posted form data (the requested items like province)        $keyword = $this->input->post('term');        $data['response'] = 'false'; //Set default response        $query = $this->Data_diagnosa->lookup($keyword); //Search DB        if( ! empty($query) )        {            $data['response'] = 'true'; //Set response            $data['message'] = array(); //Create array            foreach( $query as $row )            {                $data['message'][] = array(                                        'id'=>$row->propinsi_id,                                        'value' => $row->propinsi,                                        ''                                     );  //Add a row to array            }        }        if('IS_AJAX')        {            echo json_encode($data); //echo json string if ajax request         }        else        {            $this->load->view('autocomplete/index',$data); //Load html view of search results        }    }          }	?>